	public static boolean validateUserAuth(UserEntity user) {
		Set<RoleEntity> userRoles = user.getRoles();
		for (RoleEntity r : userRoles) {
			if (r.getName().equals("ADMIN")) {
				return false;
			}
		}
		return true;
	}
	
